import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Table = () => {
  const [tableData, setTableData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5); // Set the number of items per page

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3001/api/table");
      setTableData(response.data);
    } catch (err) {
      console.log(err, "data fetching error");
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Calculate index of the last item on the current page
  const indexOfLastItem = currentPage * itemsPerPage;
  // Calculate index of the first item on the current page
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  // Get current items
  const currentItems = tableData.slice(indexOfFirstItem, indexOfLastItem);

  // Change page
  const paginate = pageNumber => setCurrentPage(pageNumber);

  return (
    <div className='flex flex-col ml-10 mr-4 w-4/6 h-3/4'>
      <div className="rounded-l-md rounded-r-md bg-white  h-3/4">
        <div className="overflow-x-auto sm:-mx-6 lg:-mx-8">
          <div className="inline-block min-w-full py-2 sm:px-6 lg:px-8">
            <div className="overflow-hidden">
              <table className="min-w-full text-left text-sm font-light">
                <thead className="border-b bg-white font-medium dark:border-neutral-500 dark:bg-zinc-500">
                  <tr>
                    <th scope="col" className="px-6 py-4">Id</th>
                    <th scope="col" className="px-6 py-4">Name</th>
                    <th scope="col" className="px-6 py-4">Quantity</th>
                    <th scope="col" className="px-6 py-4">Price</th>
                  </tr>
                </thead>
                <tbody>
                  {currentItems.map((item) => (
                    <tr key={item?.id}
                      className="border-b bg-neutral-100 dark:border-neutral-500 dark:bg-neutral-700">
                      <td className="whitespace-nowrap px-6 py-2 font-medium">{item?.id}</td>
                      <td className="whitespace-nowrap px-6 py-2">{item?.name}</td>
                      <td className="whitespace-nowrap px-6 py-2">{item?.quantity}</td>
                      <td className="whitespace-nowrap px-6 py-2">{item?.price}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
      {/* pagination */}
      <div className="flex justify-center mt-4">
        {Array.from({ length: Math.ceil(tableData.length / itemsPerPage) }, (_, i) => (
          <button key={i} onClick={() => paginate(i + 1)} className={`px-3 py-1 mx-1 rounded ${currentPage === i + 1 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>{i + 1}</button>
        ))}
      </div>
    </div>
  );
}

export default Table;
